	            _self.on('error', handleEvent(self, 'error'));
	            _self.on('close', handleEvent(self, 'close'));
	            _self.on('timeout', handleEvent(self, 'timeout'));
	            _self.on('parseError', handleEvent(self, 'parseError'));

