	      server.once('close', _handleEvent(self, 'close'));
	      server.once('timeout', _handleEvent(self, 'timeout'));
	      server.once('error', _handleEvent(self, 'error'));
	      server.once('parseError', _handleEvent(self, 'parseError'));

